<template>
  <button
    class="sb-button"
    :class="[color, {
      'icon-left': iconLeft,
      'small': small,
      'outline': outline
    }]"
    :type="type"
    @click="onClick()"
  >
    <div
      v-if="iconName && iconLeft"
      class="icon icon-left"
      :class="'icon-' + iconName"
    />
    <div class="sb-button-label">{{ label }}</div>
    <div
      v-if="iconName && !iconLeft"
      class="icon icon-right"
      :class="'icon-' + iconName"
    />
  </button>
</template>

<script>
export default {
  name: 'SbButton',
  props: {
    /**
     * Text to display
     */
    label: {
      type: String,
      default: ''
    },
    /**
     * Icon to display
     * @see See @springboard/icons
     */
    iconName: {
      type: String,
      default: ''
    },
    /**
     * Push icon on the left side (defaut right)
     */
    iconLeft: {
      type: Boolean,
      default: false
    },
    /**
     * Icon color of the button :
     * `blue, green, red, yellow, white` (default gray)
     */
    color: {
      type: String,
      default: ''
    },
    /**
     * Small size button
     */
    small: {
      type: Boolean,
      default: false
    },
    /**
     * Outline style
     */
    outline: {
      type: Boolean,
      default: false
    },
    /**
     * Type of the button
     */
    type: {
      type: String,
      default: 'button'
    }
  },
  methods: {
    onClick () {
      /**
       * Click event
       *
       * @event click
       * @type {undefined}
       */
      this.$emit('click')
    }
  }
}
</script>

<style lang="less" scoped>
  @import "../less/variables";

  .sb-button {
    position: relative;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-size: 14px;
    line-height: 16px;
    font-weight: bold;
    font-family: RobotoCondensed;
    border: none;
    border-radius: 2px;
    background: @sb-grey-mid;
    color: @sb-black;
    min-width: 150px;
    height: 40px;
    padding: 0 36px;
    text-transform: uppercase;
    overflow: hidden;

    &:hover {
      cursor: pointer;
    }

    // button colors
    &.blue {
      background: @sb-blue;
      color: @sb-white;
    }

    &.yellow {
      background: @sb-yellow;
      color: @sb-black;
    }

    &.red {
      background: @sb-red;
      color: @sb-white;
    }

    &.green {
      background: @sb-green;
      color: @sb-white;
    }

    &.white {
      background: @sb-white;
      color: @sb-black;
    }

    &.outline {
      background: transparent;
      border: 1px solid @sb-white;
      color: @sb-white;

      &.blue {
        border-color: @sb-blue;
        color: @sb-blue;
      }

      &.yellow {
        border-color: @sb-yellow;
        color: @sb-yellow;
      }

      &.red {
        border-color: @sb-red;
        color: @sb-red;
      }

      &.green {
        border-color: @sb-green;
        color: @sb-green;
      }

      &.white {
        border-color: @sb-white;
        color: @sb-white;
      }
    }

    // active overlay
    &::before {
      content: '';
      width: 108%;
      height: 100%;
      position: absolute;
      right: -6%;
      bottom: -78%;
      transform-origin: 100% 50%;
      transform: rotate(-10deg);
      transition: all .5s cubic-bezier(.165,.84,.44,1);
      background: rgba(0,0,0,0.15);
    }

    &:hover::before {
      height: 150%;
      transform: translate3d(0,-50%,0) rotate(0);
    }

    &.icon-left::before {
      left: -6%;
      bottom: -78%;
      transform-origin: 0 50%;
      transform: rotate(10deg);
    }

    &.icon-left:hover::before {
      transform: translate3d(0,-50%,0) rotate(5deg);
    }

    // label
    .sb-button-label {
      z-index: 1;
    }

    // optional icon
    .icon {
      display: flex;
      align-items: center;
      justify-content: center;
      position: absolute;
      top: 0;
      height: 100%;
      width: 36px;
      font-weight: normal;

      &.icon-right {
        right: 0;
      }

      &.icon-left {
        left: 0;
      }
    }

    // small button
    &.small {
      height: 30px;
      min-width: 100px;
      font-size: 12px;
      padding: 0 26px;
      text-transform: initial;
      letter-spacing: 0.3px;
    }

    // button disabled
    &:disabled {
      &:hover {
        cursor: initial;
      }

      background: @sb-grey-mid;
      color: @sb-grey;

      &::before {
        display: none;
      }
    }
  }
</style>
