<template>
  <div class="sb-cookie-manager">
    <div
      v-if="showBanner"
      class="banner"
    >
      <div class="sb-text-16 content">
        <!-- @slot Banner content -->
        <slot />
      </div>
      <div class="buttons">
        <sb-button
          :label="labelButtonCustomize"
          color="blue"
          class="button-customize"
          @click="openModal()"
        />
        <sb-button
          :label="labelButtonAccept"
          color="white"
          class="button-validate"
          @click="permitAll()"
        />
      </div>
    </div>
    <sb-modal
      ref="modal-cookie-manager"
      class="modal-cookie-manager"
      :show="showModal"
      :fullscreen="modalFullscreen"
      @close="onCloseModal()"
    >
      <div
        slot="header"
        class="header"
      >
        <h1 class="sb-h4 sb-text-16 title">{{ modalTitle }}</h1>
        <div
          class="icon icon-cross-1 close-icon"
          @click="closeModal()"
        />
      </div>
      <div class="body">
        <ul class="list">
          <li
            v-for="(service, index) in services"
            :key="index"
            class="item"
          >
            <div class="item-content">
              <div class="sb-h4 sb-text-16 item-name">{{ service.name }}</div>
              <div class="sb-text-16 item-description">{{ service.description }}</div>
            </div>
            <div class="item-switch">
              <sb-switch
                :id="'switch-' + service.id"
                :ref="'switch-' + service.id"
                v-model="service.enabled"
                :name="'switch-' + service.id"
              />
            </div>
          </li>
        </ul>
      </div>
      <div
        slot="footer"
        class="footer"
      >
        <div class="buttons">
          <sb-button
            :label="labelButtonValidate"
            color="blue"
            icon-name="arrow-right"
            class="button-validate"
            @click="validate()"
          />
        </div>
      </div>
    </sb-modal>
  </div>
</template>

<script>
import SbButton from './SbButton.vue'
import SbModal from './SbModal.vue'
import SbSwitch from './SbSwitch.vue'

export default {
  name: 'SbCookieManager',
  components: {
    SbButton,
    SbModal,
    SbSwitch
  },
  props: {
    /**
     * Fullscreen modal mode.
     */
    modalFullscreen: {
      type: Boolean,
      default: false
    },
    /**
     * Display the banner or not.
     */
    showBanner: {
      type: Boolean,
      default: true
    },
    /**
     * Service object: { id: String, name: String, description: String, url: String, callback: Function, cookies: String[] }
     */
    services: {
      type: Array,
      required: true
    },
    /**
     * Label of the customize button
     */
    labelButtonCustomize: {
      type: String,
      default: ''
    },
    /**
     * Label of the accept button
     */
    labelButtonAccept: {
      type: String,
      default: ''
    },
    /**
     * Title of the services modal
     */
    modalTitle: {
      type: String,
      default: ''
    },
    /**
     * Label of the validate button
     */
    labelButtonValidate: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      showModal: false
    }
  },
  methods: {
    permitAll () {
      this.services.forEach(service => {
        service.enabled = true
      })
      this.update()
    },
    onCloseModal () {
      this.closeModal()
    },
    openModal () {
      this.showModal = true
    },
    closeModal () {
      this.showModal = false
    },
    validate () {
      this.closeModal()
      this.update()
    },
    update () {
      /**
       * Click event
       *
       * @event onChange
       * @type {object}
       */
      this.$emit('onChange', {
        showBanner: false,
        services: this.services
      })
    }
  }
}
</script>

