<template>
  <div class="textfield-container">
    <div
      v-if="label"
      class="sb-form-label textfield-label"
    >
      {{ label }}<span v-if="required">*</span>
    </div>

    <div
      class="textfield-button-wrapper"
      :class="{
        'has-button': buttonIcon,
        'floating': floating
      }"
    >
      <div
        class="textfield-input-container"
      >
        <input
          :id="id"
          :ref="id"
          v-bind="inputAttributes"
          v-model="mutableValue"
          :disabled="mutableDisabled"
          class="sb-form-input textfield-input"
          :class="{
            'error': mutableHasError,
            'disabled': mutableDisabled,
            'has-left-icon': leftIcon
          }"
          v-on="inputListeners"
        >
        <div
          v-if="leftIcon"
          class="textfield-left-icon icon"
          :class="'icon-' + leftIcon"
        />
        <div class="textfield-input-border" />
      </div>

      <div
        v-if="buttonIcon"
        class="textfield-button-container"
      >
        <button
          class="textfield-button"
          @click="onEvent('buttonClick', mutableValue)"
        >
          <div
            class="icon"
            :class="'icon-' + buttonIcon"
          />
        </button>
      </div>
    </div>

    <p
      v-if="mutableHasError && mutableErrorMessage"
      class="textfield-error-message"
    >
      {{ mutableErrorMessage }}
    </p>
  </div>
</template>

<script>
export default {
  name: 'SbInput',
  model: {
    prop: 'value',
    event: 'input'
  },
  props: {
    /**
     * Id attribute
     */
    id: {
      type: String,
      default: function () {
        return 'sb-input-' + this._uid
      }
    },
    /**
     * Input label
     */
    label: {
      type: String,
      default: ''
    },
    /**
     * Input value
     * @model
     */
    value: {
      type: [String, Number],
      default: undefined
    },
    /**
     * Error state
     */
    hasError: {
      type: Boolean,
      default: false
    },
    /**
     * Error message to display below the component
     */
    errorMessage: {
      type: String,
      default: 'This field is required'
    },
    /**
     * Defines the value as required (display error message else)
     */
    required: {
      type: Boolean,
      default: false
    },
    /**
     * Disabled attribute
     */
    disabled: {
      type: Boolean,
      default: false
    },
    /**
     * Displays an icon before the input
     * @see See @springboard/icons
     */
    leftIcon: {
      type: String,
      default: ''
    },
    /**
     * Displays a button after the input with the corresponding icon
     * @see See @springboard/icons
     */
    buttonIcon: {
      type: String,
      default: ''
    },
    /**
     * Display the input with shadow behind
     */
    floating: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      mutableHasError: this.hasError || (this.required && !this.value),
      mutableErrorMessage: this.errorMessage,
      mutableDisabled: this.disabled,
      mutableValue: this.value
    }
  },
  computed: {
    /**
     * Bubbles standards HTML Events to parent
     */
    inputListeners: function () {
      const vm = this

      return {
        // We add all the listeners from the parent
        ...this.$listeners,

        // override default behavior to ensures it works with v-model
        input: function () {
          vm.onEvent('input', vm.mutableValue)
        },
        change: function () {
          vm.onEvent('change', vm.mutableValue)
        }
      }
    },
    /**
     * Fallthrough parent attributes
     */
    inputAttributes: function () {
      return {
        // We add all the attributes from the parent
        ...this.$attrs
      }
    }
  },
  watch: {
    hasError (newValue) {
      this.mutableHasError = newValue
    },
    errorMessage (newValue) {
      this.mutableErrorMessage = newValue
    },
    disabled (newValue) {
      this.mutableDisabled = newValue
    },
    value (newValue) {
      this.mutableValue = newValue
    }
  },
  methods: {
    /**
     * Call `HTMLElement.focus(options)` on input field
     *
     * @public
     * @see https://developer.mozilla.org/en-US/docs/Web/API/HTMLElement/focus
     *
     * @param {any} options
     * @returns {void}
     */
    focus (options) {
      return this.$refs[this.id].focus(options)
    },
    /**
     * Valids input field
     *
     * @param {string|number}
     * @returns {boolean}
     */
    checkError (value) {
      // handle pattern attribute if present on parent
      // else, it shouldnt make any trouble
      const regexPattern = RegExp(this.$attrs.pattern)

      this.mutableHasError = this.hasError || (this.required && !regexPattern.test(value))
    },
    /**
     * Handles custom events, with validation
     *
     * @fires change|input|buttonClick
     * @returns {void}
     */
    onEvent (eventName, value) {
      this.checkError(value)

      /**
       * Events fired with current input value
       *
       * @event change|input|buttonClick
       * @type {string|number}
       */
      this.$emit(eventName, value)
    }
  }
}
</script>

<style lang="less" scoped>
  @import "../less/variables";

  @color-focus-line: @sb-blue;
  @color-background: @sb-white;
  @color-placeholder: @sb-grey;
  @color-border: @sb-grey;
  @color-text-input: @sb-black;
  @color-error-background: @sb-red-20;
  @color-error-focus-line: @sb-red;
  @color-disabled-background: @sb-grey-light;
  @color-icon: @sb-grey;
  @color-icon-focus: @sb-blue;
  @color-icon-focus-error: @sb-red;
  @color-icon-disabled: @sb-grey;

  .textfield-container {
    font-family: RobotoCondensed;
    padding: 10px;
    width: 100%;
    text-align: left;
    box-sizing: border-box;

    .textfield-label {
      margin-bottom: 10px;
    }

    .textfield-button-wrapper {
      display: flex;
      align-items: stretch;

      &.floating {
        box-shadow: @box-shadow-centered-2;

        .textfield-input-container,
        .textfield-button-container {
          border: none;
        }

        .textfield-button-container::after {
          border-left-color: @sb-grey-mid;
        }
      }

      &.has-button .textfield-input-container {
        border-right: none;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
      }

      .textfield-input-container {
        position: relative;
        border-radius: 2px;
        border: 1px solid @color-border;
        flex: 1;

        .textfield-input {
          width: 100%;
          padding: 10px 14px;
          outline: none;
          border: none;
          border-radius: 2px;
          background-color: @color-background;
          text-align: inherit;
          box-sizing: border-box;

          &.has-left-icon {
            padding-left: 36px;
          }

          &:focus {
            & ~ .textfield-left-icon {
              color: @color-icon-focus;
            }

            &.error ~ .textfield-left-icon {
              color: @color-icon-focus-error;
            }

            & ~ .textfield-input-border:after {
              right: 0;
            }
          }

          &:disabled {
            color: @color-icon-disabled;
            background-color: @color-disabled-background;

            & ~ .textfield-left-icon {
              color: @color-icon-disabled;
            }
          }

          &.error {
            background-color: @color-error-background;

            & ~ .textfield-input-border:after {
              border-bottom-color: @color-error-focus-line;
            }
          }

          /*
            SÃ©paration obligatoire des styles pour placeholder
            (voir https://stackoverflow.com/questions/2610497/change-an-html5-inputs-placeholder-color-with-css?utm_medium=organic&utm_source=google_rich_qa&utm_campaign=google_rich_qa)
          */
          &::-webkit-input-placeholder { /* WebKit, Blink, Edge */
            font-family: RobotoCondensed;
            font-size: 14px;
            line-height: 16px;
            color: @color-placeholder;
            opacity: 1;
          }
          &:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
            font-family: RobotoCondensed;
            font-size: 14px;
            line-height: 16px;
            color: @color-placeholder;
            opacity: 1;
          }
          &::-moz-placeholder { /* Mozilla Firefox 19+ */
            font-family: RobotoCondensed;
            font-size: 14px;
            line-height: 16px;
            color: @color-placeholder;
            opacity: 1;
          }
        }

        .textfield-left-icon {
          position: absolute;
          top: 0;
          left: 0;
          width: 36px;
          height: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
          color: @color-icon;
        }

        .textfield-input-border {
          position: relative;

          &:after {
            content: '';
            position: absolute;
            border-bottom: 2px solid @color-focus-line;
            bottom: -1px; // compensates parent border
            left: 0;
            right: 100%;
            transition: all 200ms;
          }
        }
      }

      .textfield-button-container {
        position: relative;
        display: flex;
        align-items: center;
        border-radius: 0 2px 2px 0;
        border: 1px solid @color-border;
        border-left: none;

        &::after {
          width: 10px;
          height: 100%;
          display: block;
          position: absolute;
          top: 0;
          left: -3px;
          content: '';
          background: @sb-white;
          transform: skewX(10deg);
          border-left: 1px solid @color-border;
        }

        .textfield-button {
          display: flex;
          align-items: center;
          justify-content: center;
          width: 36px;
          height: 100%;
          background: @sb-white;
          border: none;
          color: @sb-blue;
          font-size: 16px;

          &:hover {
            cursor: pointer;
          }
        }
      }
    }

    .textfield-error-message {
      color: @sb-red;
      font-size: 14px;
      line-height: 16px;
      padding-top: 10px;
      margin: 0;
    }
  }
</style>
