<template>
  <transition
    v-if="mutableShow"
    :name="fullscreen ? 'modal-fullscreen' : 'modal'"
  >
    <div
      class="sb-modal-wrapper"
      :class="{
        'fullscreen': fullscreen
      }"
      @click="clickOnWrapper($event.target)"
    >
      <div class="sb-modal-container sb-shadow-centered-3">
        <header
          v-if="this.$slots.header"
          class="sb-modal-header"
        >
          <!-- @slot Modal header -->
          <slot name="header" />
        </header>
        <main
          v-if="this.$slots.default"
          class="sb-modal-body"
        >
          <!-- @slot Modal body -->
          <slot />
        </main>
        <footer
          v-if="this.$slots.footer"
          class="sb-modal-footer"
        >
          <!-- @slot Modal footer -->
          <slot name="footer" />
        </footer>
      </div>
    </div>
  </transition>
</template>

<script>
const MODAL_OPEN_CLASS = 'sb-modal-open'

export default {
  name: 'SbModal',
  model: {
    prop: 'show',
    event: 'close'
  },
  props: {
    /**
     * Display/hide the modal
     * @model
     */
    show: {
      type: Boolean,
      default: false
    },
    /**
     * Set modal in fullscreen mode
     */
    fullscreen: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      mutableShow: this.show
    }
  },
  watch: {
    show (newValue) {
      if (newValue) {
        this.$emit('opened')
      }
      this.mutableShow = newValue
    },

    /**
     * Adds/removes a special class on the body
     * to avoid it to scroll when the modal is open
     */
    mutableShow (newValue) {
      if (newValue) {
        document.body.classList.add(MODAL_OPEN_CLASS)
      } else {
        document.body.classList.remove(MODAL_OPEN_CLASS)
      }
    }
  },
  methods: {
    close () {
      /**
       * On modal close.
       * Value should always be `false`
       *
       * @event close
       * @type {boolean}
       */
      this.$emit('close', false)
      this.mutableShow = false
    },

    /**
     * Click on wrapper (outside the modal)
     * will close this one
     */
    clickOnWrapper (target) {
      // not directly on the wrapper div, out
      if (!target.classList.contains('sb-modal-wrapper')) {
        return
      }

      this.close()
    }
  }
}
</script>

<style lang="less" scoped>
  @import "../less/variables";

  .sb-modal-wrapper {
    font-family: RobotoCondensed;
    position: fixed;
    z-index: 1000;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: transparent;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all .3s ease;

    @media @tablet {
      &:not(.fullscreen) {
        background-color: rgba(38, 38, 40, 0.7);

        .sb-modal-container {
          max-height: 90%;
          width: 80%;
          height: auto;
          border-radius: 2px;
        }
      }
    }

    .sb-modal-container {
      position: relative;
      max-height: 100%;
      height: 100%;
      width: 100%;
      border-radius: 0;
      margin: 0 auto;
      background-color: #fff;
      overflow: hidden;
      display: flex;
      flex-direction: column;
      transition: all .3s ease;

      .sb-modal-body {
        overflow-y: scroll;
      }
    }
  }

  /*
  * The following styles are auto-applied to elements with
  * transition="modal" when their visibility is toggled
  * by Vue.js.
  *
  * You can easily play with the modal transition by editing
  * these styles.
  */

  .modal-enter,
  .modal-leave-active {
    transform: translateY(100%);

    @media @tablet {
      transform: none;
      opacity: 0;

      .sb-modal-container {
        transform: scale(1.1);
      }
    }
  }

  .modal-fullscreen-enter,
  .modal-fullscreen-leave-active {
    transform: translateY(100%);
  }
</style>
