<template>
  <div
    class="navbar-container"
    :class="color"
  >
    <div class="navbar-button-icon">
      <router-link
        v-if="to"
        :to="to"
      >
        <div :class="`icon icon-${iconLeft}`" />
      </router-link>
      <div
        v-else
        class="sb-clickable"
      >
        <div
          :class="`icon icon-${iconLeft}`"
          @click="onClick()"
        />
      </div>
    </div>
    <div class="navbar-content sb-text-18">
      <!-- @slot Nav bar content -->
      <slot />
    </div>
  </div>
</template>

<script>
export default {
  name: 'SbNavBar',
  props: {
    /**
     * Left button link.
     * If filled `click` event won't be fired
     */
    to: {
      type: String,
      default: ''
    },
    /**
     * Left button icon.
     */
    iconLeft: {
      type: String,
      default: 'arrow2-left'
    },
    /**
     * Background color : `blue` or `white` (default)
     */
    color: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      // generate a random name to allow usage of multiple instance on the same page
      buttonName: Math.random().toString(36).substring(2, 15)
    }
  },
  methods: {
    onClick () {
      /**
       * On left icon click
       *
       * @event click
       * @type {undefined}
       */
      this.$emit('click')
    }
  }
}
</script>

<style lang="less" scoped>
  @import "../less/variables";

  .navbar-container {
    position: relative;
    line-height: 24px;
    padding: 15px 0;
    color: @sb-black;
    text-align: center;
    font-family: RobotoCondensed;
    overflow: hidden; // avoid ::after to overflow

    &.blue {
      color: @sb-white;
      background: @sb-blue;

      a, .navbar-content {
        color: inherit;
      }
    }

    &.clear {
      color: @sb-white;
      background: transparent;

      a, .navbar-content {
        color: inherit;
      }
    }

    .navbar-content {
      min-height: 21px;
    }

    .navbar-button-icon {
      position: absolute;
      left: 0;
      top: 0;
      bottom: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 20px;
      padding: 16px 20px;
      z-index: 1;

      &::after {
        width: 65px;
        height: 100%;
        display: block;
        position: absolute;
        top: 0;
        left: -5px;
        content: '';
        background: rgba(0, 0, 0, 0.2);
        transform: skewX(-10deg);
        z-index: -1;
      }
    }
  }
</style>
