<template>
  <div class="switch-container">
    <div
      v-if="label"
      class="sb-form-label switch-label"
    >
      {{ label }}
    </div>
    <label
      :for="id"
      class="switch-content-wrapper"
    >
      <div
        :class="{
          'switch-slot': this.$slots.default
        }"
      >
        <!-- @slot Switch left clickable label -->
        <slot />
      </div>
      <div class="switch">
        <input
          :id="id"
          type="checkbox"
          :name="name"
          :checked="value"
          @click="toggle()"
        >
        <span class="slider round">
          <div class="checkmark" />
        </span>
      </div>
    </label>
  </div>
</template>

<script>
export default {
  name: 'SbSwitch',
  model: {
    prop: 'value',
    event: 'toggle'
  },
  props: {
    /**
     * The id for the switch.
     */
    id: {
      type: String,
      default: function () {
        return 'sb-switch-' + this._uid
      }
    },
    /**
     * The name for the switch.
     */
    name: {
      type: String,
      default: ''
    },
    /**
     * Label
     */
    label: {
      type: String,
      default: ''
    },
    /**
     * Switch value
     * @model
     */
    value: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    toggle () {
      /**
       * Toggle event
       *
       * @event toggle
       * @type Boolean
       */
      this.$emit('toggle', !this.value)
    }
  }
}
</script>

<style lang="less" scoped>
@import '../less/variables';

@slider-size: 18px;

.switch-container {
  display: inline-block;
  padding: 10px;

  .switch-label {
    margin-bottom: 10px;
  }

  .switch-content-wrapper {
    display: flex;
    align-items: center;
  }

  .switch-slot {
    cursor: pointer;
    margin-right: 10px;
  }

  .switch {
    position: relative;
    display: block;
    width: 36px;
    height: 22px;

    input {
      opacity: 0;
      width: 0;
      height: 0;

      &:checked + .slider {
        background-color: #B2D9ED;
      }

      &:checked + .slider:before {
        left: 2px;
        bottom: 2px;
        height: @slider-size;
        width: @slider-size;
        border: 1px solid @sb-blue;
        transform: translateX(14px);
        box-sizing: border-box;
      }

      &:checked + .slider .checkmark:before {
        width: 4px;
        height: 8px;
        border-width: 0 1px 1px 0;
        transform: translateX(14px) rotate(45deg);
      }
    }

    .slider {
      position: absolute;
      cursor: pointer;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background-color: #E9EBED;
      transition: .4s;

      &.round {
        border-radius: 34px;

        &:focus {
          outline: none;
        }

        &:before {
          border-radius: 50%;
        }
      }

      &:before {
        position: absolute;
        content: '';
        height: @slider-size;
        width: @slider-size;
        left: 2px;
        bottom: 2px;
        background-color: #fff;
        transition: .4s;
      }

      .checkmark {
        position: relative;
        height: @slider-size;
        width: @slider-size;

        &:before {
          position: absolute;
          top: 2px;
          bottom: 0;
          right: 0;
          left: 4px;
          margin: auto;
          content: '';
          width: 0;
          height: 0;
          border: solid @sb-blue;
          border-width: 0;
          transform: rotate(45deg);
          transition: .4s;
        }
      }
    }
  }
}
</style>
