import 'chai/register-should'
import { shallowMount } from '@vue/test-utils'

import { SbSwitch } from '../src/components'

const LABEL = 'Click here'

describe('SbSwitch.vue', () => {
  it('Should_EmitToggleEvent_When_SwitchIsClicked', () => {
    const wrapper = shallowMount(SbSwitch)

    wrapper.find('.switch-content-wrapper').trigger('click')

    wrapper.emitted().toggle.should.exist
    wrapper.emitted().toggle[0].should.be.eql([true])
  })

  it('Should_InputLabelBeClickHere_When_LabelIsSetToClickHere', () => {
    const wrapper = shallowMount(SbSwitch, {
      propsData: {
        label: LABEL
      }
    })

    wrapper.contains('.switch-label').should.be.true
    wrapper.find('.switch-label').text().should.be.equal(LABEL)
  })
})
